import React, {
    createContext,
    useState,
    useEffect,
    ReactNode,
    FC,
} from 'react';
import { auth, googleProvider } from './firebase';
import {
    signInWithPopup,
    signOut,
    User,
    onAuthStateChanged,
} from 'firebase/auth';
import { Conversation } from './types/types';


// Define the shape of the AuthContext
export interface AuthContextType {
    user: User | null;
    signIn: () => Promise<void>;
    logOut: () => Promise<void>;
    getToken: () => Promise<string | null>;
    loading: boolean;
    conversations: Conversation[];
    updateConversations: (newConversations: Conversation[]) => void;
}

// Create the AuthContext with a default value of undefined.
// Consumers should handle the undefined case.
export const AuthContext = createContext<AuthContextType | undefined>(
    undefined
);

// Define the props for AuthProvider
interface AuthProviderProps {
    children: ReactNode;
}

// AuthProvider component
export const AuthProvider: FC<AuthProviderProps> = ({ children }) => {
    const [user, setUser] = useState<User | null>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [conversations, setConversations] = useState<Conversation[]>([]);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
            setUser(currentUser);
            setLoading(false);
            if (!currentUser) {
                setConversations([]);
            }
        });

        // Cleanup subscription on unmount
        return () => unsubscribe();
    }, []);

    // Sign in with Google
    const signIn = async (): Promise<void> => {
        try {
            await signInWithPopup(auth, googleProvider);
        } catch (error) {
            console.error('Error signing in with Google', error);
            // Optionally, you can add error handling logic here
        }
    };

    // Log out the current user
    const logOut = async (): Promise<void> => {
        try {
            await signOut(auth);
            setConversations([]);
        } catch (error) {
            console.error('Error signing out', error);
            // Optionally, you can add error handling logic here
        }
    };

    // Get the current user's ID token
    const getToken = async (): Promise<string | null> => {
        if (user) {
            return await user.getIdToken();
        }
        return null;
    };

    // Update the conversations state
    const updateConversations = (
        newConversations: Conversation[]
    ): void => {
        setConversations(newConversations);
    };

    return (
        <AuthContext.Provider
            value={{
                user,
                signIn,
                logOut,
                getToken,
                loading,
                conversations,
                updateConversations,
            }}
        >
            {children}
        </AuthContext.Provider>
    );
};
